import React from 'react'
import '../css/TestCategory.css'
import {Link,Prompt} from 'react-router-dom';
import ModalSignin from '../components/ModalSignin';

const token = localStorage.getItem('token');


const CardCategory = ({id,urlPhoto,sub_name,description}) => {

    const onClose = () => 
    {
        if(!token){
            window.alert("You have to login");
            <ModalSignin />

        }
    }
    return (
        <div class="container">
            <div class="card">
                <div class="face face1">
                    <div class="content">
                        <div class="img-subcategory">
                            <img src={urlPhoto} alt="about category group"/>
                        </div>
                            <h3>{sub_name}</h3>
                    </div>
                </div>
                        <div class="face face2">
                            <div class="content">
                                <p>{description}</p>
                                <Link to={`/timeline/${sub_name}`} onClick={() => onClose()} >Read more</Link>
                            </div>
                    </div>
            </div>
        </div>
    )
}

export default CardCategory
