import React, { useState } from 'react'
import {Link} from 'react-router-dom';
import logo from './images/logo.png'
import '../css/Navbar.css'
import { FaBars, FaTimes } from 'react-icons/fa'
import ModalSignin from './ModalSignin';
import Register from './Register';
import { FiMenu, FiX } from "react-icons/fi";



class Navbar extends React.Component {

    constructor(props) {
      super(props);
    }
    // setting mobile new
    componentDidMount(){
    const [click, setClick] = useState(false)
    const handleClick = () => setClick(!click)
    const closeMobileMenu = () => setClick(false);

    // setting Modal
    const [modalOpen, setModalOpen] = useState(false);
    const [modalOpenLogin, setModalOpenLogin] = useState(false);
    console.log(click);




    // change nav color when scrolling
    const [navbar, setNavbar] = useState(false);
    const changeBackgroud = () => {
        console.log(window.scrollY)
    }
    window.addEventListener('scroll', changeBackgroud);

    const closeMenu = () => setClick(false)
    }
 render(){

    return (
        <>
          <nav className="navbar">
            <div className="navbar-container">
                <Link to="/main" className="navbar-logo">
                    <img src={logo} alt='logo' />
                </Link>
                <div className='menu-icon' onClick={handleClick}>
                    {click ? (<FaTimes size={30} style={{ color: '#ffffff' }} />)
                        : (<FaBars size={30} style={{ color: '#ffffff' }} />)}
                </div>
                <ul className={click ? 'nav-memu active' : 'nav-menu'}>
                    <li className='nav-item'>
                        <Link to='/main' className='nav-links' onClick={closeMobileMenu}>
                            Home
                        </Link>
                    </li>
                    <li className='nav-item'>
                        <Link to='/category' className='nav-links' onClick={closeMobileMenu}>
                            Category
                        </Link>
                    </li>
                    <li className='nav-item'>
                        <Link to='/' className='nav-links' onClick={closeMobileMenu}>
                            About
                        </Link>
                    </li>
                    <button  className='signin-btn' cursorhover='true'
                        onClick={() => {
                            setModalOpenLogin(true);
                        }}>
                        <span cursorhover='true'>Sign in</span>                    
                    </button>
                    {modalOpenLogin && <ModalSignin setOpenModalLogin={setModalOpenLogin} />}

                    <button className='signup-btn' cursorhover='true'
                        onClick={() => {
                            setModalOpen(true);
                        }}>
                        <span cursorhover='true'>Sign up</span>                    
                    </button>
                    {modalOpen && <Register setOpenModal={setModalOpen} />}
                </ul>

                <div className="mobile-menu" onClick={handleClick}>
                    {click ? (
                        <FiX />
                    ) : (
                        <FiMenu />
                    )}
                </div>

            </div>

          </nav>
        </>

    )
 }
}

export default Navbar;
