import { FiMenu, FiX } from "react-icons/fi";
import React, { useState } from 'react'
import bgImg from '../video/rocket.mp4'
import './Home.css'
import ModalSignin from "./ModalSignin";
import ReactTypingEffect from 'react-typing-effect';
import './Text.css'



const Home = () =>{
    const [modalOpen, setModalOpen] = useState(false);
    const [click, setClick] = useState(false);
    const handleClick = () => setClick(!click);
    console.log(click);
    const closeMobileMenu = () => setClick(false);
    return(
        <div className='home'>
            <video autoPlay loop muted>
                <source src={bgImg} type="video/mp4" />
            </video>
            <div className='nav-bar'>
                <div className="header">
                    <ul className={click ? "menu active" : "menu"}>
                        <button className='signin-btn' cursorhover='true'
                                onClick={() => {
                                    setModalOpen(true);
                                  }}>
                            <span cursorhover='true'>Sign in</span>                    
                        </button>
                        {modalOpen && <ModalSignin setOpenModal={setModalOpen} />}
                    </ul>
                </div>

                <div class="hero-main" id="hero">
                    <div className="heading-section">
                        <div className="intro">
                            <ReactTypingEffect text={["Hello.", "Welcome to","Univ E-leaning","Trying to do better."]}
                                speed={100}
                                typingDelay={1000}
                                eraseDelay={1000}
                                eraseSpeed={100}
                                cursorRenderer={cursor => <h1 className="cursor">{cursor}</h1>}
                                displayTextRenderer={(text, i) => {
                                return (<p className="name">{text}</p>)}} />
                        </div>
                    </div>
                </div>

                <div className="mobile-menu" onClick={handleClick}>
                    {click ? (
                        <FiX />
                    ) : (
                        <FiMenu />
                    )}
                </div>
            </div>
        </div>

    )
}

export default Home
