import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Button from "@material-ui/core/Button";

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    flexGrow: 1,
  },
  large: {
    width: theme.spacing(20),
    height: theme.spacing(20),
  },
}));





class TestCategory extends React.Component {
    constructor(props) {
      super(props);

      this.state = {
          categorys: [],
          subCategorys: [],
          DataisLoaded: false 
      };
  }

  componentDidMount() {
      Promise.all([
          fetch("http://127.0.0.1:8000/api/catagory"),
          fetch("http://127.0.0.1:8000/api/subcategory")
      ])
      .then(([res1, res2]) => Promise.all([res1.json(), res2.json()]))
      .then(([data1, data2]) => this.setState({
          categorys: data1, 
          subCategorys: data2,
          DataisLoaded: true
      }));
  }
  render(){

    const { DataisLoaded, categorys,subCategorys } = this.state;
    console.log(categorys,subCategorys);
    const arrSubcategory1 = [],arrSubcategory2 = [],arrSubcategory3 = [],arrSubcategory4 = [];
    const arrSubcategory5 = [],arrSubcategory6 = [],arrSubcategory7 = [],arrSubcategory8 = [];
    const arrSubcategory = [];


    if(DataisLoaded){
      {subCategorys.data.map((data,idx) => {
        if (data.catagoriesID === 1) {
          arrSubcategory1.push(data.sub_name)
        }

        if (data.catagoriesID === 2) {
          arrSubcategory2.push(data.sub_name)
        }

        if (data.catagoriesID === 3) {
          arrSubcategory3.push(data.sub_name)
        }

        if (data.catagoriesID === 4) {
          arrSubcategory4.push(data.sub_name)
        }

        if (data.catagoriesID === 5) {
          arrSubcategory5.push(data.sub_name)
        }

        if (data.catagoriesID === 6) {
          arrSubcategory6.push(data.sub_name)
        }

        if (data.catagoriesID === 7) {
          arrSubcategory7.push(data.sub_name)
        }

        if (data.catagoriesID === 8) {
          arrSubcategory8.push(data.sub_name)
        }

      })}
    }

    arrSubcategory.push(arrSubcategory1,arrSubcategory2,arrSubcategory3,arrSubcategory4,arrSubcategory5,arrSubcategory6,arrSubcategory7,arrSubcategory8)

    console.log(arrSubcategory1,arrSubcategory2,arrSubcategory3,arrSubcategory4,arrSubcategory5,arrSubcategory6,arrSubcategory7,arrSubcategory8);
    console.log(arrSubcategory)

    if (!DataisLoaded) return <div>
        <h1> Pleses wait for loading data </h1> </div> ;

    return (
<div className="category">
        <div className="header">
          <h3>Discover your interesting</h3>
          <div className="header-menu">
              <p>Course</p>
              <p>Topics</p>
          </div>
          <div className="category-menu" style={{textAlign: 'left'}}>
            {categorys.data.map((data,idx) => 
            <div className="category-title" style={{textAlign: 'left'}}>
              <h3 key={data.id}>{data.name}</h3>
              <div className="sub-category">
                <div className="card-sub">
                {
                  arrSubcategory.map((datasub,id) =>  {
                    if(id == idx){
                      return {datasub}, // เก็บ subarray[categoty]
                      datasub.map((datasubcat,id) =>{
                        return {datasubcat}, //return value[i]
                        <body>
                          <div class="container">
                              <div class="card">
                                  <div class="face face1">
                                      <div class="content">
                                          <img src="https://github.com/Jhonierpc/WebDevelopment/blob/master/CSS%20Card%20Hover%20Effects/img/design_128.png?raw=true" />
                                          <h3>{datasub[id]}</h3>
                                      </div>
                                  </div>
                                  <div class="face face2">
                                      <div class="content">
                                          <p>{datasub.idx}</p>
                                              <a href="#">Read More</a>
                                      </div>
                                  </div>
                              </div>
                          </div>
                      </body>
                      })
                    }
                    // if(id == idx)
                    //   return <p>{datasub}</p>
                  }
                )}
                </div>
              </div>
            </div>
            )}
          </div>
        </div>
      </div>
    )
  }
}

export default Post
