import React, { useState, useEffect } from 'react'
import {Link} from 'react-router-dom';
import logo from './images/logo.png'
import '../css/Navbar.css'
import ModalSignin from './ModalSignin';
import Register from './Register';
import { FiMenu, FiX } from "react-icons/fi";

function NavbarLogin() {

    // setting mobile new
    const [click, setClick] = useState(false)
    const handleClick = () => setClick(!click)
    const closeMobileMenu = () => setClick(false);

    // setting Modal
    const [modalOpen, setModalOpen] = useState(false);
    const [modalOpenLogin, setModalOpenLogin] = useState(false);
    console.log(click);


    // change nav color when scrolling
    const [color, setColor] = useState(false);
    const changeColor = () => {
        if(window.scrollY >= 80) {
            setColor(true);
        } else {
            setColor(false);
        }
    };
    window.addEventListener('scroll', changeColor);
    const userName = localStorage.getItem('user');
    /*const closeMenu = () => setClick(false)*/


    return (
        <>
            <nav className={color ? 'navbar navbar-bg' : 'navbar'}>
                <div className="navbar-container">
                    <Link to="/" className="navbar-logo">
                        <img src={logo} alt='logo' />
                    </Link>

                    <div className='menu-icon' onClick={handleClick}>
                        <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
                    </div>

                    <ul className={click ? 'nav-memu active' : 'nav-menu'}>
                        <li className='nav-item'>
                            <Link to='/' className='nav-links' onClick={closeMobileMenu}>
                                Home
                            </Link>
                        </li>

                        <li className='nav-item'>
                            <Link to='#catagory' className='nav-links' onClick={closeMobileMenu}>
                                Category
                            </Link>
                        </li>
                        <li className='nav-item'>
                            <Link to='#About' className='nav-links' onClick={closeMobileMenu}>
                                About Us
                            </Link>
                        </li>

                        <li className='nav-item'>
                            <Link to='#{userName}' className='nav-links' onClick={closeMobileMenu}>
                                {userName}
                            </Link>
                        </li>





                    </ul>

                    {modalOpen && <Register setOpenModal={setModalOpen} />}
                    {modalOpenLogin && <ModalSignin setOpenModalLogin={setModalOpenLogin} />}


                    <div className="mobile-menu" onClick={handleClick}>
                        {click ? (
                            <FiX />
                        ) : (
                            <FiMenu />
                        )}
                    </div>

                </div>

            </nav>
        </>

    )
}

export default NavbarLogin;
