import React from 'react'
import '../css/TestCategory.css'
import {Link,Prompt} from 'react-router-dom';

const CardCategory = ({id,urlPhoto,sub_name,description}) => {
    return (
        <div class="container">
            <div class="card">
                <div class="face face1">
                    <div class="content">
                        <div class="img-subcategory">
                            <img src={urlPhoto} alt="about category group"/>
                        </div>
                            <h3>{sub_name}</h3>
                    </div>
                </div>
                        <div class="face face2">
                            <div class="content">
                                <p>{description}</p>
                                <Prompt message={(location, action) => {
                                    if (action === 'POP') {
                                        console.log("Backing up...")
                                    }
                                    return location.pathname.startsWith("/app")
                                    ? true
                                    : `Are you sure you want to go to ${location.pathname}?`
  }}
/>
                                <Link to={`/timeline/${sub_name}`} >Read more</Link>
                            </div>
                    </div>
            </div>
        </div>
    )
}

export default CardCategory
